use super::*;
use ethers_signers::{LocalWallet, Signer};
use reth_primitives::{Address, Transaction, TxKind};
use std::collections::HashMap;

/// A wallet manager that handles multiple wallets.
pub struct WalletManager {
    wallets: HashMap<Address, LocalWallet>,
}

impl WalletManager {
    /// Creates a new wallet manager.
    pub fn new() -> Self {
        Self { wallets: HashMap::new() }
    }

    /// Creates a new wallet and adds it to the manager.
    pub fn create_wallet(&mut self, password: &str) -> Result<Address> {
        let wallet = LocalWallet::new(&mut rand::thread_rng());
        let address = wallet.address();
        self.wallets.insert(address, wallet);
        Ok(address)
    }

    /// Unlocks a wallet with the given password.
    pub fn unlock_wallet(&mut self, address: Address, password: &str) -> Result<()> {
        // In a real implementation, this would decrypt the wallet using the password
        Ok(())
    }

    /// Signs a transaction with the given wallet.
    pub fn sign_transaction(
        &self,
        address: Address,
        transaction: Transaction,
    ) -> Result<Transaction> {
        let wallet = self.wallets.get(&address).ok_or(Error::WalletNotFound)?;
        let signature = wallet.sign_transaction(&transaction)?;
        Ok(transaction.with_signature(signature))
    }
}
