// Assuming getPayload and config are available
import { getPayload } from 'payload'
import config from '@payload-config'

export async function createPost(data: any) { // Use specific type for data if possible
  try {
    const payload = await getPayload({ config })
    // Attempt to create a post using Payload Local API
    return await payload.create({ collection: 'posts', data })
  } catch (error) {
    // Log the detailed error on the server
    console.error('Error creating post:', error)
    // Return a generic, structured error response to the client
    return { error: 'Failed to create post' }
  }
}
