  id: string,
  paypalMockResponse?: string,
  paypalRequestId?: string,
  prefer?: string,
  paypalClientMetadataId?: string,
  paypalAuthAssertion?: string,
  body?: OrderCaptureRequest,
  requestOptions?: RequestOptions
): Promise<ApiResponse<Order>>
