version: "3.9"

services:
  permify:
    image: "ghcr.io/permify/permify"
    command: "serve"
    ports:
      - "3476:3476"
      - "3478:3478"
    environment:
      - PERMIFY_DATABASE_ENGINE=postgres
      - PERMIFY_POSTGRES_CONNECTION_STRING=postgres://postgres:postgres@postgres:5432/permify?sslmode=disable
      - PERMIFY_CACHE_ENGINE=redis
      - PERMIFY_REDIS_ADDRESS=redis:6379
    depends_on:
      postgres:
        condition: service_healthy
      redis:
        condition: service_healthy

