type service struct {
    permify permify.PermissionClient
    // Your service dependencies
}

func NewServer(client permify.PermissionClient) pb.YourServiceServer {
    return &service{
        permify: client,
    }
}

func (s *service) YourEndpoint(ctx context.Context, req *pb.YourRequest) (*pb.YourResponse, error) {
    // Extract the user ID from the context
    userID, err := extractUserID(ctx)
    if err != nil {
        return nil, status.Error(codes.Unauthenticated, "authentication required")
    }

    // Perform the permission check
    resp, err := s.permify.Check(ctx, &permify.CheckRequest{
        TenantId: "t1",
        Metadata: &v1.PermissionCheckRequestMetadata{
            SchemaVersion: "1",
            SnapToken:     "",
            Depth:         20,
        },
        Entity: &v1.Entity{
            Type: "user",
            Id:   userID,
        },
        Permission: "create",
        Resource: &v1.Resource{
            Type: "repository",
            Id:   req.RepositoryId,
        },
    })

    if err != nil {
        return nil, status.Errorf(codes.Internal, "permission check failed: %v", err)
    }

    if resp.Can != permify.CheckResponse_ALLOWED {
        return nil, status.Error(codes.PermissionDenied, "insufficient permissions")
    }

    // Permission is granted, proceed with your service logic
    return &pb.YourResponse{
        // Response data
    }, nil
}

