// Lookup is a function that returns the resources of a specific type that a subject can access.
func (q *query) Lookup(ctx context.Context, request *v1.LookupRequest) (*v1.LookupResponse, error) {
	span, ctx := tracer.Start(ctx, "query.Lookup", trace.WithAttributes(attribute.String("component", "query")))
	defer span.End()

	// Handle streaming query mode 
	if q.mode == storage.STREAM {
		entities, err := q.lookup(ctx, span, request)
		if err != nil {
			return nil, err
		}

		return &v1.LookupResponse{
			Entities: entities,
		}, nil
	}

	_, err := q.ds.LoadCache(ctx)
	if err != nil {
		return nil, err
	}

	entities, err := q.lookup(ctx, span, request)
	if err != nil {
		return nil, err
	}

	return &v1.LookupResponse{
		Entities: entities,
	}, nil
}
