TITLE: Checking User Permission in Go Authorization System
DESCRIPTION: Implements the Check function that determines if a subject has permission to access a resource. It returns the result of the permission check along with any errors. The function handles both synchronous and streaming query modes.
SOURCE: https://github.com/Permify/permify/blob/master/docs/docs/api-overview/permission/check-api.md#2025-04-17_snippet_0

LANGUAGE: go
CODE:
```
// Check is a function that determines whether the subject has the requested permission on the given resource or not.
func (q *query) Check(ctx context.Context, request *v1.CheckRequest) (*v1.CheckResponse, error) {
	span, ctx := tracer.Start(ctx, "query.Check", trace.WithAttributes(attribute.String("component", "query")))
	defer span.End()

	// Handle stream query mode.
	if q.mode == storage.STREAM {
		subject := request.GetSubject()
		decisionParameters := request.GetDepth()

		permissionChecks, _, err := q.check(ctx, span, request)
		if err != nil {
		recorder.RecordErrorCheckRequest(
			q.recordSpanExporter, subject.GetType(), subject.GetId(), request.GetResource().GetType(), request.GetPermission(), nil, err)
			return nil, err
		}

		outcomes := utils.Map(permissionChecks, func(permissionCheck *corev1.PermissionCheck) bool {
			return permissionCheck.GetResult() == corev1.CheckResult_CHECK_RESULT_ALLOWED
		})

		canAccess := false
		for _, outcome := range outcomes {
			if outcome {
				canAccess = true
				break
			}
		}

		// Distributed tracing span annotations
		span.SetAttributes(attribute.Int("depth", int(decisionParameters)))
		span.SetAttributes(attribute.Bool("can_access", canAccess))

		res := &v1.CheckResponse{
			Can: canAccess,
		}

		recorder.RecordCheckRequest(
			q.recordSpanExporter, subject.GetType(), subject.GetId(), request.GetResource().GetType(), request.GetPermission(), res.GetCan(), nil)

		return res, nil
	}

	// Synchronous query mode.
	subject := request.GetSubject()
	cache, err := q.ds.LoadCache(ctx)
	if err != nil {
		recorder.RecordErrorCheckRequest(
			q.recordSpanExporter, subject.GetType(), subject.GetId(), request.GetResource().GetType(), request.GetPermission(), nil, err)
		return nil, err
	}

	decisionParameters := request.GetDepth()

	permissionChecks, metadata, err := q.check(ctx, span, request)
	if err != nil {
		recorder.RecordErrorCheckRequest(
			q.recordSpanExporter, subject.GetType(), subject.GetId(), request.GetResource().GetType(), request.GetPermission(), nil, err)
		return nil, err
	}

	outcomes := utils.Map(permissionChecks, func(permissionCheck *corev1.PermissionCheck) bool {
		return permissionCheck.GetResult() == corev1.CheckResult_CHECK_RESULT_ALLOWED
	})

	canAccess := false
	for _, outcome := range outcomes {
		if outcome {
			canAccess = true
			break
		}
	}

	// Distributed tracing span annotations
	span.SetAttributes(attribute.Int("depth", int(decisionParameters)))
	span.SetAttributes(attribute.Bool("can_access", canAccess))

	tunableParameters := v1.TunableParameters{
		Depth:                  decisionParameters,
		FullyQualifiedFilters: []*v1.FullyQualifiedFilter{},
	}

	res := &v1.CheckResponse{
		Can:               canAccess,
		Metadata:          metadata,
		TunableParameters: &tunableParameters,
	}

	// Add check result caching capability when enabled
	if q.cacheEnabled {
		ctx = contextx.WithTransactionID(ctx, utils.CalcHash(utils.Hash(request.String())))
		gocache.Write(ctx, gocache.WithCheckResponse(res))
	}

	recorder.RecordCheckRequest(
		q.recordSpanExporter, subject.GetType(), subject.GetId(), request.GetResource().GetType(), request.GetPermission(), res.GetCan(), nil)

	return res, nil
}
```

