TITLE: Filtering Schema Attributes using SQL in Go
DESCRIPTION: Generates a SQL query to filter schema attributes based on given criteria. The function builds a parameterized query with filters for tables, entity types, and schema versions, returning the attribute records and any query errors.
SOURCE: https://github.com/Permify/permify/blob/master/docs/docs/use-cases/multi-tenancy.md#2025-04-17_snippet_1

LANGUAGE: go
CODE:
```
func (s *sqlStorage) FilterAttributes(ctx context.Context, filter *v1.AttributeFilter, opts ...pagination.Option) ([]*base.AttributeFilter, *pagination.PageToken, error) {
	query := `
		SELECT entity_type, attribute, schema_version
		FROM $1
		WHERE 1=1
		$2
		$3
		$4
		ORDER BY entity_type, attribute, schema_version
		$5
	`

	table := s.attributeTable()

	args := make([]interface{}, 0, 10)
	args = append(args, table)

	entityTypeFilter := ""
	if filter.GetEntityType() != "" {
		entityTypeFilter = "AND entity_type = $"
		args = append(args, filter.GetEntityType())
		entityTypeFilter = fmt.Sprintf(entityTypeFilter, len(args))
	}

	attributeFilter := ""
	if filter.GetAttribute() != "" {
		attributeFilter = "AND attribute = $"
		args = append(args, filter.GetAttribute())
		attributeFilter = fmt.Sprintf(attributeFilter, len(args))
	}

	schemaVersionFilter := ""
	if filter.GetSchemaVersion() != "" {
		schemaVersionFilter = "AND schema_version = $"
		args = append(args, filter.GetSchemaVersion())
		schemaVersionFilter = fmt.Sprintf(schemaVersionFilter, len(args))
	}

	pag := pagination.NewPaginator(opts...)

	tokenFilter := ""
	if pag.GetToken() != nil {
		token := pag.GetToken()
		tokenFilter = "LIMIT $"
		args = append(args, token.Limit)
		tokenFilter = fmt.Sprintf(tokenFilter, len(args))
	}

	query = strings.Replace(query, "$1", "$1", 1)
	query = strings.Replace(query, "$2", entityTypeFilter, 1)
	query = strings.Replace(query, "$3", attributeFilter, 1)
	query = strings.Replace(query, "$4", schemaVersionFilter, 1)
	query = strings.Replace(query, "$5", tokenFilter, 1)

	rows, err := s.db.QueryContext(ctx, query, args...)
	if err != nil {
		return nil, nil, fmt.Errorf("error executing the query to filter attributes: %w", err)
	}

	defer rows.Close()

	attributes := make([]*base.AttributeFilter, 0)

	for rows.Next() {
		var entityType, attribute, schemaVersion string

		err := rows.Scan(&entityType, &attribute, &schemaVersion)
		if err != nil {
			return nil, nil, fmt.Errorf("error scanning attribute row: %w", err)
		}

		attributes = append(attributes, &base.AttributeFilter{
			EntityType:    entityType,
			Attribute:     attribute,
			SchemaVersion: schemaVersion,
		})
	}

	if err := rows.Err(); err != nil {
		return nil, nil, fmt.Errorf("error iterating through attribute rows: %w", err)
	}

	return attributes, pag.GetNewToken(len(attributes)), nil
}
```

