TITLE: Validating Schema Internal Consistency in Go
DESCRIPTION: A Go function to validate the internal consistency of a Permify schema. It iterates through entity types, relation references, and permissions to ensure all references are valid within the schema.
SOURCE: https://github.com/Permify/permify/blob/master/docs/docs/api-overview/schema/read-schema.md#2025-04-17_snippet_1

LANGUAGE: go
CODE:
```
func (s *SchemaService) CheckSchemaConsistency(ctx context.Context, req *v1.CheckConsistencyRequest) (*v1.CheckConsistencyResponse, error) {
    // Creates schema object
    sch, err := s.invoker.Call(ctx, NewCheckSchemaPrepare(req, s.schemaReader))
    if err != nil {
        return nil, err
    }

    // Transform schema
    schema := sch.(*base.SchemaDefinition)

    // Create consistent report
    report := &v1.ConsistencyReport{
        RelationErrors:   []*v1.RelationError{},
        PermissionErrors: []*v1.PermissionError{},
    }

    // Check internal consistency per entity
    for entityType, entity := range schema.EntityDefinitions {
        // Check relation reference
        for _, reference := range entity.GetRelationReferences() {
            entityDefinition, exists := schema.EntityDefinitions[reference.GetEntityType()]
            if !exists {
                report.RelationErrors = append(report.RelationErrors, &v1.RelationError{
                    EntityType: entityType,
                    Relation:   reference.GetRelation(),
                    Message:    fmt.Sprintf("relation refers to entity type '%s' which is not defined in the schema", reference.GetEntityType()),
                })
                continue
            }

            relationDefinition, exists := entityDefinition.GetRelation(reference.GetRelation())
            if !exists {
                report.RelationErrors = append(report.RelationErrors, &v1.RelationError{
                    EntityType: entityType,
                    Relation:   reference.GetRelation(),
                    Message:    fmt.Sprintf("relation refers to relation '%s' which is not defined in entity type '%s'", reference.GetRelation(), reference.GetEntityType()),
                })
            } else {
                if relationDefinition.GetType() != base.RelationTypeDirected {
                    report.RelationErrors = append(report.RelationErrors, &v1.RelationError{
                        EntityType: entityType,
                        Relation:   reference.GetRelation(),
                        Message:    fmt.Sprintf("relation refers to relation '%s.%s' which is not of type RELATION_TYPE_DIRECTED", reference.GetEntityType(), reference.GetRelation()),
                    })
                }
            }
        }

        // Check permission references
        for _, permissionName := range entity.GetPermissions() {
            permission, _ := entity.GetPermission(permissionName)

            for _, reference := range permission.GetEntityReferences() {
                entityDefinition, exists := schema.EntityDefinitions[reference.GetEntityType()]
                if !exists {
                    report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                        EntityType: entityType,
                        Permission: permissionName,
                        Message:    fmt.Sprintf("permission refers to entity type '%s' which is not defined in the schema", reference.GetEntityType()),
                    })
                    continue
                }

                switch reference.GetTypeOfReference() {
                case base.EntityReferenceTypePermission:
                    permissionDefinition, exists := entityDefinition.GetPermission(reference.GetReference())
                    if !exists {
                        report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                            EntityType: entityType,
                            Permission: permissionName,
                            Message:    fmt.Sprintf("permission refers to permission '%s' which is not defined in entity type '%s'", reference.GetReference(), reference.GetEntityType()),
                        })
                    } else {
                        if permissionDefinition.HasCycle() {
                            report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                                EntityType: entityType,
                                Permission: permissionName,
                                Message:    fmt.Sprintf("permission refers to permission '%s.%s' which has a cycle", reference.GetEntityType(), reference.GetReference()),
                            })
                        }
                    }
                case base.EntityReferenceTypeRelation:
                    relationDefinition, exists := entityDefinition.GetRelation(reference.GetReference())
                    if !exists {
                        report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                            EntityType: entityType,
                            Permission: permissionName,
                            Message:    fmt.Sprintf("permission refers to relation '%s' which is not defined in entity type '%s'", reference.GetReference(), reference.GetEntityType()),
                        })
                    } else {
                        // check referenced relation attributes
                        if reference.GetRelationAttribute() != nil {
                            attr := reference.GetRelationAttribute()
                            if relationDefinition.GetType() != base.RelationTypeSetAttribute {
                                report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                                    EntityType: entityType,
                                    Permission: permissionName,
                                    Message:    fmt.Sprintf("permission refers to relation '%s.%s' with attribute, but relation is not of type RELATION_TYPE_SET_ATTRIBUTES", reference.GetEntityType(), reference.GetReference()),
                                })
                            } else if !relationDefinition.ContainsAttribute(attr.GetName()) {
                                report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                                    EntityType: entityType,
                                    Permission: permissionName,
                                    Message:    fmt.Sprintf("permission refers to relation '%s.%s' with attribute '%s', but attribute is not defined in relation", reference.GetEntityType(), reference.GetReference(), attr.GetName()),
                                })
                            }
                        }
                    }
                case base.EntityReferenceTypeAttribute:
                    attributeDefinition, exists := entityDefinition.GetAttribute(reference.GetReference())
                    if !exists {
                        report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                            EntityType: entityType,
                            Permission: permissionName,
                            Message:    fmt.Sprintf("permission refers to attribute '%s' which is not defined in entity type '%s'", reference.GetReference(), reference.GetEntityType()),
                        })
                    } else {
                        if attributeDefinition.GetType() != base.AttributeTypeBoolean {
                            report.PermissionErrors = append(report.PermissionErrors, &v1.PermissionError{
                                EntityType: entityType,
                                Permission: permissionName,
                                Message:    fmt.Sprintf("permission refers to attribute '%s.%s' which is not of type ATTRIBUTE_TYPE_BOOLEAN", reference.GetEntityType(), reference.GetReference()),
                            })
                        }
                    }
                }
            }
        }
    }

    return &v1.CheckConsistencyResponse{
        ConsistencyReport: report,
    }, nil
}
```

