TITLE: Configuring Envoy for PDP Cluster
DESCRIPTION: Envoy configuration file that sets up a load balancer for 7 PDP shards using ring hash load balancing policy. The configuration includes listener setup, routing rules, and endpoint definitions for each PDP shard.
SOURCE: https://github.com/permitio/docs/blob/master/docs/how-to/manage-data/pdp-sharding.mdx#2025-04-17_snippet_0

LANGUAGE: yaml
CODE:
```
admin:
  address:
    socket_address: { address: 0.0.0.0, port_value: 9901 }
static_resources:
  listeners:
    - name: sdk_requests
      address:
        socket_address: { address: 0.0.0.0, port_value: 10000 }
      filter_chains:
        - filters:
            - name: envoy.filters.network.http_connection_manager
              typed_config:
                "@type": type.googleapis.com/envoy.extensions.filters.network.http_connection_manager.v3.HttpConnectionManager
                stat_prefix: ingress_http
                codec_type: AUTO
                route_config:
                  name: local_route
                  virtual_hosts:
                    - name: local_service
                      domains: ["*"]
                      routes:
                        - match: { prefix: "/" }
                          route:
                            cluster: pdp_cluster
                            hash_policy:
                              - header:
                                  header_name: X-Tenant-Id
                http_filters:
                  - name: envoy.filters.http.router
                    typed_config:
                      "@type": type.googleapis.com/envoy.extensions.filters.http.router.v3.Router
  clusters:
    - name: pdp_cluster
      connect_timeout: 1s
      type: STRICT_DNS
      lb_policy: RING_HASH
      ring_hash_lb_config:
        maximum_ring_size: 1024
      dns_lookup_family: V4_ONLY
      load_assignment:
        cluster_name: pdp_cluster
        endpoints:
          - lb_endpoints:
              - endpoint:
                  address:
                    socket_address:
                      address: pdp-shard-0
                      port_value: 7000
                metadata:
                  filter_metadata:
                    envoy.lb:
                      hash_key: "0"
              - endpoint:
                  address:
                    socket_address:
                      address: pdp-shard-1
                      port_value: 7000
                metadata:
                  filter_metadata:
                    envoy.lb:
                      hash_key: "1"
              - endpoint:
                  address:
                    socket_address:
                      address: pdp-shard-2
                      port_value: 7000
                metadata:
                  filter_metadata:
                    envoy.lb:
                      hash_key: "2"
              - endpoint:
                  address:
                    socket_address:
                      address: pdp-shard-3
                      port_value: 7000
                metadata:
                  filter_metadata:
                    envoy.lb:
                      hash_key: "3"
              - endpoint:
                  address:
                    socket_address:
                      address: pdp-shard-4
                      port_value: 7000
                metadata:
                  filter_metadata:
                    envoy.lb:
                      hash_key: "4"
              - endpoint:
                  address:
                    socket_address:
                      address: pdp-shard-5
