    WithPdpUrl("http://localhost:7766").
    WithProxyFactsViaPdp().
    WithFactsSyncTimeout(10).             // Optional: Uses PDP default if not specified
    WithFactsSyncTimeoutPolicy("ignore"). // Optional: Uses PDP default if not specified
