LANGUAGE: javascript
CODE:
```
{
  //...
  devServer: {
    client: {
      overlay: {
        errors: false,
        warnings: false,
        runtimeErrors: (error: Error) => {
          if (error.message.includes("ResizeObserver loop")) {
            return false;
          } else {
            return true;
          }
        },
      },
    },
  },
}
