import requests

url = "https://text.pollinations.ai/Hello%20world?model=openai-audio&voice=nova"

try:
    response = requests.get(url)
    response.raise_for_status()

    if response.headers['Content-Type'] == 'audio/mpeg':
        with open('hello_audio.mp3', 'wb') as f:
            f.write(response.content)
        print("Audio saved to hello_audio.mp3")
    else:
        print(f"Unexpected content type: {response.headers['Content-Type']}")
        print("Response:", response.text)

except requests.exceptions.RequestException as e:
    print(f"Error during TTS request: {e}")
