export const useUserFriends = defineLoader(
  async (route, { loaders: [userData] }) => {
    const friends = await getFriends(user.value.id)
    return { ...userData.value, friends }
  },
  {
    // explicit dependencies
    waitFor: [useUserData],
  }
)
```

