TITLE: Dynamic Home Page with Prisma User Queries
DESCRIPTION: Enhanced home page implementation using Prisma Client to fetch and display users dynamically from the database.
SOURCE: https://github.com/prisma/docs/blob/main/content/800-guides/170-react-router-7.mdx#2025-04-21_snippet_10

LANGUAGE: tsx
CODE:
```
import type { Route } from "./+types/home";
import prisma from '~/lib/prisma'

export function meta({}: Route.MetaArgs) {
  return [
    { title: "New React Router App" },
    { name: "description", content: "Welcome to React Router!" },
  ];
}

export async function loader() {
  const users = await prisma.user.findMany();
  return { users };
}

export default function Home({ loaderData }: Route.ComponentProps) {
  const { users } = loaderData;
  return (
    <div className="min-h-screen flex flex-col items-center justify-center -mt-16">
      <h1 className="text-4xl font-bold mb-8 font-[family-name:var(--font-geist-sans)]">
        Superblog
      </h1>
