      }
    }
  }
}
```

LANGUAGE: javascript
CODE:
```
import { fileURLToPath } from 'node:url'

export default (ctx) => {
  return {
    build: {
      extendViteConf (viteConf, { isServer, isClient }) {
        Object.assign(viteConf.resolve.alias, {
          utils: fileURLToPath(new URL('./src/utils', import.meta.url))
        })
      }
    }
  }
}
