TITLE: Implementing SpotlightPopover with Custom Footer in Blade UI Framework
DESCRIPTION: This snippet demonstrates how to implement a guided tour using the SpotlightPopover component with a custom footer. It shows the complete implementation including defining steps, managing state, and handling step changes. The example showcases integration with a DashboardPage component that contains the highlightable elements.

LANGUAGE: jsx
CODE:
```
import { SpotlightPopover, SpotlightPopoverFooter } from '@razorpay/blade/components';
import type { SpotlightPopoverSteps } from '@razorpay/blade/components';

const Footer = ({ activeStep, totalSteps, goToNext, goToPrevious, stopTour }) => {
  const isLast = activeStep === totalSteps - 1;
  const isFirst = activeStep === 0;
  return (
    <SpotlightPopoverFooter
      activeStep={activeStep}
      totalSteps={totalSteps}
      actions={{
        primary: isLast
          ? { text: 'Done', onClick: stopTour }
          : { text: 'Next', onClick: goToNext },
        secondary: isFirst
          ? undefined
          : { text: 'Prev', onClick: goToPrevious },
      }}
    />
  );
};

const steps: SpotlightPopoverSteps = [
  {
    name: 'step-1',
    title: 'Step 1',
    content: ({ activeStep }) => (
      <Text>Some content {activeStep}</Text>
    ),
    footer: Footer,
  },
  {
    name: 'step-2',
    title: 'Step 2',
    content: ({ activeStep }) => (
      <Text>Some content {activeStep}</Text>
    ),
    footer: Footer,
  },
  {
    name: 'step-3',
    title: 'Step 3',
    content: ({ gotToNext, activeStep }) => (
      <Box>
        <Text>Some content {activeStep}</Text>
        <Button onClick={gotToNext}>Next</Button>
      </Box>
    ),
    footer: Footer,
  },
];

const App = () => {
  const [IsOpen, setIsOpen] = useState(false);
  const [activeStep, setActiveStep] = useState(0);

  const handleOpenChange = ({ isOpen }) => {
    setIsOpen(isOpen);
    // reset the active step so if the user opens the tour again,
    // it starts from the first step
    setActiveStep(0);
  };

  const handleFinish = () => {
    // do something when the tour is finished
    // ie: send an analytic event
    // or: close the tour
    // or: reset the active step
  };

  const handleStepChange = (step: number) => {
    setActiveStep(step);
    // do something with the step
    // ie: send an analytic event
  };

  return (
    <Box>
      <Button onClick={() => setIsOpen(true)}>Show SpotlightPopover</Button>
      <SpotlightPopover
        steps={steps}
        isOpen={isOpen}
        onOpenChange={handleOpenChange}
        onFinish={handleFinish}
        onStepChange={handleStepChange}
        activeStep={activeStep}
      >
        <DashboardPage />
      </SpotlightPopover>
    </Box>
  );
};

// In some other file: DashboardPage.tsx
const DashboardPage = () => {
  return (
    <Box>
      <SpotlightPopoverStep name="step-1">
        <Button>Click me</Button>
      </SpotlightPopoverStep>

      <Box>
        <SpotlightPopoverStep name="step-2">
          <Text>Some content</Text>
        </SpotlightPopoverStep>
      </Box>

      <Card>
        <CardBody>
          <SpotlightPopoverStep name="step-3">
            <Box>
              <Text>Some content</Text>
              <Text>Some content</Text>
              <Text>Some content</Text>
            </Box>
          </SpotlightPopoverStep>
        </CardBody>
      </Card>
    </Box>
  );
};
```

