TITLE: Creating Custom Tab Bar with UI Kitten Components
DESCRIPTION: Implements a custom tab bar using UI Kitten components for the TODO navigator, handling tab selection and navigation.
SOURCE: https://github.com/react-navigation/react-navigation.github.io/blob/main/blog/2019-11-04-using-react-navigation-5-with-ui-kitten.md#2025-04-23_snippet_3

LANGUAGE: typescript
CODE:
```
import React from 'react';
import { TabBar, Tab, Divider, TabElement } from '@ui-kitten/components';
import { SafeAreaLayout, SaveAreaInset, SafeAreaLayoutElement } from '../../components/safe-area-layout.component';
import { Toolbar } from '../../components/toolbar.component';

export const TodoTabBar = (props): SafeAreaLayoutElement => {

  const onTabSelect = (index: number): void => {
    const selectedTabRoute: string = props.state.routeNames[index];
    props.navigation.navigate(selectedTabRoute);
  };

  const createNavigationTabForRoute = (route): TabElement => {
    const { options } = props.descriptors[route.key];
    return (
      <Tab
        key={route.key}
        title={options.title}
        icon={options.tabBarIcon}
      />
    );
  };

  return (
    <SafeAreaLayout insets={SaveAreaInset.TOP}>
      <Toolbar title='React Navigation Ex 🐱'/>
      <TabBar selectedIndex={props.state.index} onSelect={onTabSelect}>
        {props.state.routes.map(createNavigationTabForRoute)}
      </TabBar>
      <Divider/>
    </SafeAreaLayout>
  );
};
```

