export function loadPosts(userId) {
  return function (dispatch, getState) {
    const { posts } = getState()
    if (posts[userId]) {
      return
    }

    dispatch({
      type: 'LOAD_POSTS_REQUEST',
      userId
    })

    fetch(`http://myapi.com/users/${userId}/posts`).then(
      response =>
        dispatch({
          type: 'LOAD_POSTS_SUCCESS',
          userId,
          response
        }),
      error =>
        dispatch({
          type: 'LOAD_POSTS_FAILURE',
          userId,
          error
        })
    )
  }
}
