TITLE: Implementing Websocket and Cache Management in Notifications Slice
DESCRIPTION: This snippet demonstrates the implementation of the onCacheEntryAdded lifecycle handler for the getNotifications endpoint. It sets up a websocket connection, handles incoming messages, updates the cache, and manages the lifecycle of the cache entry.
SOURCE: https://github.com/reduxjs/redux/blob/master/docs/tutorials/essentials/part-8-rtk-query-advanced.md#2025-04-07_snippet_21

LANGUAGE: typescript
CODE:
```
import {
  createEntityAdapter,
  createSlice,
  createSelector,
  // highlight-start
  createAction,
  isAnyOf
  // highlight-end
} from '@reduxjs/toolkit'
// omit imports and other code

// highlight-next-line
const notificationsReceived = createAction<ServerNotification[]>('notifications/notificationsReceived')

export const apiSliceWithNotifications = apiSlice.injectEndpoints({
  endpoints: builder => ({
    getNotifications: builder.query<ServerNotification[], void>({
      query: () => '/notifications',
      // highlight-start
      async onCacheEntryAdded(arg, lifecycleApi) {
        // create a websocket connection when the cache subscription starts
        const ws = new WebSocket('ws://localhost')
        try {
          // wait for the initial query to resolve before proceeding
          await lifecycleApi.cacheDataLoaded

          // when data is received from the socket connection to the server,
          // update our query result with the received message
          const listener = (event: MessageEvent<string>) => {
            const message: {
              type: 'notifications'
              payload: ServerNotification[]
            } = JSON.parse(event.data)
            switch (message.type) {
              case 'notifications': {
                lifecycleApi.updateCachedData(draft => {
                  // Insert all received notifications from the websocket
                  // into the existing RTKQ cache array
                  draft.push(...message.payload)
                  draft.sort((a, b) => b.date.localeCompare(a.date))
                })

                // Dispatch an additional action so we can track "read" state
                lifecycleApi.dispatch(notificationsReceived(message.payload))
                break
              }
              default:
                break
            }
          }

          ws.addEventListener('message', listener)
        } catch {
          // no-op in case `cacheEntryRemoved` resolves before `cacheDataLoaded`,
          // in which case `cacheDataLoaded` will throw
        }
        // cacheEntryRemoved will resolve when the cache subscription is no longer active
        await lifecycleApi.cacheEntryRemoved
        // perform cleanup steps once the `cacheEntryRemoved` promise resolves
        ws.close()
      }
    })
    // highlight-end
  })
})

export const { useGetNotificationsQuery } = apiSliceWithNotifications

// highlight-start
const matchNotificationsReceived = isAnyOf(
  notificationsReceived,
  apiSliceWithNotifications.endpoints.getNotifications.matchFulfilled,
)
// highlight-end

// omit other code

const notificationsSlice = createSlice({
  name: 'notifications',
  initialState,
  reducers: { /* omit reducers */  },
  extraReducers(builder) {
    // highlight-next-line
    builder.addMatcher(matchNotificationsReceived, (state, action) => {
