TITLE: Implementing File Upload Form Component
DESCRIPTION: Creates a form component with file upload handling using parseFormData and upload handler function.
SOURCE: https://github.com/remix-run/react-router/blob/main/docs/how-to/file-uploads.md#2025-04-22_snippet_2

LANGUAGE: tsx
CODE:
```
import {
  type FileUpload,
  parseFormData,
} from "@mjackson/form-data-parser";

export async function action({
  request,
}: ActionFunctionArgs) {
  const uploadHandler = async (fileUpload: FileUpload) => {
    if (fileUpload.fieldName === "avatar") {
      // process the upload and return a File
    }
  };

  const formData = await parseFormData(
    request,
    uploadHandler
  );
  // 'avatar' has already been processed at this point
  const file = formData.get("avatar");
}

export default function Component() {
  return (
    <form method="post" encType="multipart/form-data">
      <input type="file" name="avatar" />
      <button>Submit</button>
    </form>
  );
}
