import { remult } from "remult";
import { Task } from "./models";

export const TaskList = () => {
  const [tasks, setTasks] = useState<Task[]>([]);

  useEffect(() => {
    remult.repo(Task).find().then(setTasks);
  }, []);

  const addTask = async (title: string) => {
    const newTask = await remult.repo(Task).insert({ title });
    setTasks([...tasks, newTask]);
  };

  const setCompleted = async (task: Task, completed: boolean) => {
    const updatedTask = await remult.repo(Task).save({ ...task, completed });
    setTasks(tasks.map((t) => (t.id === task.id ? updatedTask : t)));
  };

  return (
    <div>
      {/* Render tasks and input for new tasks */}
    </div>
  );
};
