{
  tasks.map(task => {
    const setTask = (value: Task) =>
      setTasks(tasks => tasks.map(t => (t === task ? value : t)))

    const setCompleted = async (completed: boolean) =>
      setTask(await taskRepo.save({ ...task, completed }))

    const setTitle = (title: string) => setTask({ ...task, title })

