var make = context.Provider;

var Provider = {
  make: make
};

var ThemeContext = {
  context: context,
  Provider: Provider
};

function App$ThemedButton(props) {
  var theme = React.useContext(context);
  var match;
  switch (theme) {
    case "dark" :
        match = [
          "#ffffff",
          "#222222"
        ];
        break;
    case "light" :
        match = [
          "#000000",
          "#eeeeee"
        ];
        break;
    default:
      match = [
        "#000000",
        "#eeeeee"
      ];
  }
  var style = {
    backgroundColor: match[1],
    color: match[0]
  };
