TITLE: Initializing Rete.js Editor with Angular Integration
DESCRIPTION: This code snippet initializes a Rete.js editor with Angular integration. It sets up the editor, creates nodes with input controls, establishes connections, and configures the rendering using rete-angular-plugin.
SOURCE: https://github.com/retejs/retejs.org/blob/main/src/content/en/examples/1.basic/3.angular.md#2025-04-20_snippet_0

LANGUAGE: typescript
CODE:
```
import { createEditor, NodeEditor } from 'rete'
import { AreaPlugin, AreaExtensions } from 'rete-area-plugin'
import { ConnectionPlugin, Presets as ConnectionPresets } from 'rete-connection-plugin'

import { AngularPlugin, AngularArea2D, Presets as AngularPresets } from 'rete-angular-plugin'

import { DataflowEngine } from 'rete-engine'

type Schemes = {
  node: { inputs: { input: number }, outputs: { output: number } }
  conn: { data: { text: string } }
}
type AreaExtra = AngularArea2D<Schemes>

export async function createEditorAngular(container: HTMLElement) {
  const socket = AngularPresets.classic.socket

  const editor = await createEditor<Schemes, AreaExtra>()
  const area = new AreaPlugin<Schemes, AreaExtra>(container)
  const connection = new ConnectionPlugin<Schemes, AreaExtra>()
  const render = new AngularPlugin<Schemes, AreaExtra>({
    inports: AngularPresets.classic.inports,
    outports: AngularPresets.classic.outports,
    controls: AngularPresets.classic.controls
  })
  const engine = new DataflowEngine<Schemes>()

  AreaExtensions.selectableNodes(area, AngularPresets.classic.selectable)

  editor.use(area)
  area.use(connection)
  area.use(render)

  AreaExtensions.simpleNodesOrder(area)

  editor.addPipe(context => {
    if (context.type === 'connectioncreate') {
      console.log('connection created', context.data)
    }
    return context
  })

  connection.addPreset(ConnectionPresets.classic.setup())

  const a = await editor.addNode({
    id: 'a',
    label: 'A',
    inputs: { input: {} },
    outputs: { output: {} },
    position: [0, 0]
  })
  const b = await editor.addNode({
    id: 'b',
    label: 'B',
    inputs: { input: {} },
    outputs: { output: {} },
    position: [300, 0]
  })

  await editor.addConnection({
    source: a.id,
    sourceOutput: 'output',
    target: b.id,
    targetInput: 'input'
  })

  await area.translate(a.id, { x: 0, y: 0 })
  await area.translate(b.id, { x: 300, y: 0 })

  AreaExtensions.zoomAt(area, editor.getNodes())

  return {
    destroy: () => area.destroy()
  }
}
