TITLE: Implementing Custom Controls with React.js in Rete.js
DESCRIPTION: This code demonstrates how to create custom controls including a button and radial progress indicator in Rete.js with React.js. The example shows control synchronization where the button sets a random value and the progress indicator displays the current value state.
SOURCE: https://github.com/retejs/retejs.org/blob/main/src/content/en/examples/2.controls/1.react.md#2025-04-20_snippet_0

LANGUAGE: tsx
CODE:
```
import { ClassicPreset, GetSchemes, NodeEditor } from "rete";
import { AreaExtensions, AreaPlugin } from "rete-area-plugin";
import {
  ReactArea2D,
  ReactPlugin,
  ReactArea2DProps,
  Presets as ReactPresets
} from "rete-react-plugin";
import { createRoot } from "react-dom/client";
import { useState, useEffect } from 'react';

type Node = ClassicPreset.Node;
type Connection<N extends Node> = ClassicPreset.Connection<N, N>;
type Controls = {number: ClassicPreset.InputControl<"number">}

type Schemes = GetSchemes<Node, Connection<Node>, Controls>;

function RadialProgressControl(props: { value: number | null }) {
  const progress = props.value || 0;
  const normalizedProgress = Math.min(Math.max(0, progress), 100) / 100;
  const radius = 40;
  const circumference = 2 * Math.PI * radius;
  const strokeDashoffset = circumference * (1 - normalizedProgress);

  const progressColor = `hsl(${normalizedProgress * 120}, 80%, 50%)`;

  return (
    <div style={{ position: 'relative', width: '100px', height: '100px', margin: '0 auto' }}>
      <svg width="100" height="100" viewBox="0 0 100 100">
        <circle
          cx="50"
          cy="50"
          r={radius}
          fill="transparent"
          stroke="#e6e6e6"
          strokeWidth="8"
        />
        <circle
          cx="50"
          cy="50"
          r={radius}
          fill="transparent"
          stroke={progressColor}
          strokeWidth="8"
          strokeDasharray={circumference}
          strokeDashoffset={strokeDashoffset}
          strokeLinecap="round"
          transform="rotate(-90 50 50)"
        />
        <text x="50" y="55" textAnchor="middle" fontSize="16" fill="#333">
          {Math.round(normalizedProgress * 100)}%
        </text>
      </svg>
    </div>
  );
}

function ButtonControl(props: { emit: (value: number) => void }) {
  return (
    <button
      style={{
        borderRadius: '4px',
        padding: '6px 12px',
        backgroundColor: '#4CAF50',
        color: 'white',
        border: 'none',
        cursor: 'pointer',
        fontSize: '14px',
        fontWeight: 'bold',
        boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
        transition: 'background-color 0.3s',
        margin: '10px auto',
        display: 'block'
      }}
      onClick={() => props.emit(Math.random() * 100)}
    >
      Random Value
    </button>
  );
}

class NumberNode extends ClassicPreset.Node {
  width = 180;
  height = 270;

  constructor(position: { x: number; y: number }) {
    super('Number');
    this.position = position;

    const control = new ClassicPreset.InputControl('number', {
      initial: 0,
      change: (val) => {
        control.value = Math.min(Math.max(0, val), 100);
        this.update();
      }
    });
    this.addControl('number', control);
  }
}

function PreviewNode(props: { 
  node: NumberNode
  bindSocket: ReactArea2DProps["bindSocket"]
  bindControl: ReactArea2DProps["bindControl"],
  controls: ReactArea2DProps["controls"]
}) {
  const { node, bindSocket, bindControl, controls } = props;
  const [value, setValue] = useState<number | null>(null);
  const control = controls.get('number');

  const emitValue = (value: number) => {
    if (control) {
      const input = control.component.props as any;
      input.onChange(value);
    }
  }

  useEffect(() => {
    const onChange = () => {
      if (control) {
        const input = control.component.props as any;
        setValue(input.value);
      }
    }
    onChange();
    node.update = onChange;

    return () => {
      node.update = () => {};
    }
  }, []);

  return (
    <>
      <div className="node-header">{node.label}</div>
      <div style={{ padding: '15px' }}>
        <div className="control">
          {controls.get('number') && controls.get('number')?.component}
        </div>
        <RadialProgressControl value={value} />
        <ButtonControl emit={emitValue} />
      </div>
    </>
  );
}

export async function createEditor(container: HTMLElement) {
  const editor = new NodeEditor<Schemes>();
  const area = new AreaPlugin<Schemes, ReactArea2D<Schemes>>(container);
  const reactRender = new ReactPlugin<Schemes, ReactArea2D<Schemes>>();

  reactRender.addPreset(ReactPresets.classic.setup({
    customize: {
      node: (data) => {
        if (data.payload instanceof NumberNode) {
          return PreviewNode
        }
      }
    }
  }));

  AreaExtensions.selectableNodes(area, AreaExtensions.selector(), {
    accumulating: AreaExtensions.accumulateOnCtrl()
  })

  editor.addPipe(context => {
    if (context.type === 'nodecreated') {
      const node = context.data
      if (node instanceof NumberNode) {
        requestAnimationFrame(() => editor.trigger({type: 'process' }))
      }
    }
    return context
  })

  const render = reactRender.createRoot(createRoot);

  area.use(render);

  AreaExtensions.simpleNodesOrder(area);

  const node = new NumberNode({ x: 0, y: 0 });

  await editor.addNode(node);

  AreaExtensions.zoomAt(area, editor.getNodes());

  return {
    destroy: () => area.destroy()
  };
}

```

