TITLE: Configuring Angular Controls with Rete.js
DESCRIPTION: TypeScript module implementing Angular controls in Rete.js. Sets up a button for random value generation and a radial progress indicator that synchronizes with an input field value.
SOURCE: https://github.com/retejs/retejs.org/blob/main/src/content/en/examples/2.controls/3.angular.md#2025-04-20_snippet_0

LANGUAGE: typescript
CODE:
```
import { NodeEditor, GetSchemes, ClassicPreset } from 'rete';
import { AreaPlugin, AreaExtensions } from 'rete-area-plugin';
import {
  ConnectionPlugin,
  Presets as ConnectionPresets
} from 'rete-connection-plugin';
import { AngularPlugin, Presets, AngularArea2D } from 'rete-angular-plugin/16';

type Schemes = GetSchemes<
  ClassicPreset.Node,
  ClassicPreset.Connection<ClassicPreset.Node, ClassicPreset.Node>
>;
type AreaExtra = AngularArea2D<Schemes>;

export async function createEditor(container: HTMLElement) {
  const editor = new NodeEditor<Schemes>();
  const area = new AreaPlugin<Schemes, AreaExtra>(container);
  const connection = new ConnectionPlugin<Schemes, AreaExtra>();
  const render = new AngularPlugin<Schemes, AreaExtra>();

  AreaExtensions.selectableNodes(area, AreaExtensions.selector({}));

  connection.addPreset(ConnectionPresets.classic.setup());

  render.addPreset(Presets.classic.setup());
  render.addPreset(Presets.contextMenu.setup());

  editor.use(area);
  area.use(connection);
  area.use(render);

  const a = new ClassicPreset.Node('A');
  a.addControl(
    'string',
    new ClassicPreset.InputControl('text', { initial: '10' })
  );
  a.addControl(
    'button',
    new ClassicPreset.ButtonControl('random', () => {
      const value = Math.random().toFixed(2);
      const input = a.controls.string as ClassicPreset.InputControl<'text'>;

      input.setValue(value);
    })
  );
  a.addControl(
    'progress',
    new ClassicPreset.InputControl('progress', { initial: '0.5' })
  );

  await editor.addNode(a);

  const output = area.nodeViews.get(a.id)?.element as HTMLElement;

  output.style.background = 'linear-gradient(#5d6670, #5d6670)';

  return {
    destroy: () => editor.destroy()
  };
}
```

