TITLE: Implementing Context Menu in Rete.js Node Editor
DESCRIPTION: This code demonstrates how to set up a Rete.js editor with context menu functionality. It initializes the editor with various plugins including the context menu plugin, and configures both global context menus for node creation and node-specific context menus with options for deletion and copying.
SOURCE: https://github.com/retejs/retejs.org/blob/main/src/content/en/examples/9.context-menu.md#2025-04-20_snippet_0

LANGUAGE: typescript
CODE:
```
import { createRoot } from "react-dom/client";
import { NodeEditor, GetSchemes, ClassicPreset } from "rete";
import { AreaPlugin, AreaExtensions } from "rete-area-plugin";
import { ConnectionPlugin } from "rete-connection-plugin";
import {
  ReactRenderPlugin,
  Presets as ReactPresets
} from "rete-react-render-plugin";
import { ContextMenuPlugin, Presets as ContextMenuPresets } from "rete-context-menu-plugin";

type Schemes = GetSchemes<
  ClassicPreset.Node,
  ClassicPreset.Connection<ClassicPreset.Node, ClassicPreset.Node>
>;
type AreaExtra = AreaExtensions.SelectionArea;

const socket = new ClassicPreset.Socket("socket");

class NumberNode extends ClassicPreset.Node {
  width = 180;
  height = 120;

  constructor(initial: number, change?: (value: number) => void) {
    super("Number");
    this.addOutput("value", new ClassicPreset.Output(socket, "Number"));
    this.addControl(
      "value",
      new ClassicPreset.InputControl("number", {
        initial,
        change
      })
    );
  }
}

class AddNode extends ClassicPreset.Node {
  width = 180;
  height = 195;

  constructor() {
    super("Add");
    this.addInput("a", new ClassicPreset.Input(socket, "A"));
    this.addInput("b", new ClassicPreset.Input(socket, "B"));
    this.addOutput("value", new ClassicPreset.Output(socket, "Number"));
    this.addControl(
      "result",
      new ClassicPreset.InputControl("number", {
        readonly: true
      })
    );
  }
}

class FilterNode extends ClassicPreset.Node {
  width = 180;
  height = 195;

  constructor() {
    super("Filter");
    this.addInput("a", new ClassicPreset.Input(socket, "A"));
    this.addOutput("value", new ClassicPreset.Output(socket, "Number"));
    this.addControl(
      "condition",
      new ClassicPreset.InputControl("text", {
        initial: "> 10"
      })
    );
  }
}

export async function createEditor(container: HTMLElement) {
  const editor = new NodeEditor<Schemes>();
  const area = new AreaPlugin<Schemes, AreaExtra>(container);
  const connection = new ConnectionPlugin<Schemes, AreaExtra>();
  const render = new ReactRenderPlugin<Schemes, AreaExtra>({ createRoot });
  const contextMenu = new ContextMenuPlugin<Schemes>({});

  AreaExtensions.selectableNodes(area, AreaExtensions.selector(), {
    accumulating: AreaExtensions.accumulateOnCtrl()
  });

  render.addPreset(ReactPresets.classic.setup());

  connection.addPreset(ConnectionPlugin.classic.setup());

  editor.use(area);
  area.use(connection);
  area.use(render);
  area.use(contextMenu);

  contextMenu.addPreset((
    // global context menu with nodes that can be added
    ContextMenuPresets.classic.setup([
      [
        "Add",
        {
          label: "Number",
          async handler() {
            const node = new NumberNode(1);

            await editor.addNode(node);
            area.translate(node.id, area.area.pointer);
          }
        }
      ],
      [
        "Add",
        {
          label: "Operations",
          items: [
            [
              "Operations",
              {
                label: "Add",
                async handler() {
                  const node = new AddNode();

                  await editor.addNode(node);
                  area.translate(node.id, area.area.pointer);
                }
              }
            ],
            [
              "Operations",
              {
                label: "Filter",
                async handler() {
                  const node = new FilterNode();

                  await editor.addNode(node);
                  area.translate(node.id, area.area.pointer);
                }
              }
            ]
          ]
        }
      ]
    ])
  ));

  // node context menu
  contextMenu.addPreset(
    ContextMenuPresets.classic.setup([
      [
        "Node",
        {
          label: "Delete",
          handler({ node }) {
            editor.removeNode(node.id);
          }
        }
      ],
      [
        "Node",
        {
          label: "Clone",
          async handler({ node }) {
            if (!node) return;

            const clonedNode =
              node instanceof AddNode
                ? new AddNode()
                : node instanceof FilterNode
                ? new FilterNode()
                : new NumberNode(1);

            await editor.addNode(clonedNode);
            area.translate(clonedNode.id, { x: node.x + 10, y: node.y + 10 });
          }
        }
      ]
    ])
  );

  const n1 = new NumberNode(1);
  const n2 = new NumberNode(1);
  const add = new AddNode();

  await editor.addNode(n1);
  await editor.addNode(n2);
  await editor.addNode(add);

  area.translate(n1.id, { x: -150, y: 0 });
  area.translate(n2.id, { x: -150, y: 150 });
  area.translate(add.id, { x: 30, y: 70 });

  await editor.addConnection(new ClassicPreset.Connection(n1, "value", add, "a"));
  await editor.addConnection(new ClassicPreset.Connection(n2, "value", add, "b"));

  AreaExtensions.zoomAt(area, editor.getNodes());

  return editor;
}
```

