TITLE: Initializing Rete.js Editor with Svelte Controls
DESCRIPTION: This code initializes a Rete.js editor with Svelte rendering and custom controls. It sets up a node with an input control, a button for randomizing the input value, and a radial progress indicator.
SOURCE: https://github.com/retejs/retejs.org/blob/main/src/content/en/examples/2.controls/4.svelte.md#2025-04-20_snippet_0

LANGUAGE: typescript
CODE:
```
import { createEditor, BaseSchemes } from 'rete';
import { AreaPlugin, AreaExtensions } from 'rete-area-plugin';
import { SveltePlugin, Presets as SveltePresets } from 'rete-svelte-plugin';

import CustomNode from './CustomNode.svelte';
import Connection from './Connection.svelte';
import Button from './Button.svelte';
import Progress from './Progress.svelte';

type Schemes = BaseSchemes & {
  // define your schemes if necessary
};

export async function createDemoEditor(container: HTMLElement) {
  const editor = await createEditor<Schemes>();
  const area = new AreaPlugin<Schemes>(container);
  const sveltePlugin = new SveltePlugin<Schemes>();

  AreaExtensions.selectableNodes(area, AreaExtensions.selector(), {
    accumulating: AreaExtensions.accumulateOnCtrl()
  });

  const render = SveltePresets.classic.setup([
    SveltePresets.classic.addPreset<Schemes>({
      node: CustomNode,
      connection: Connection
    })
  ]);

  editor.use(area);
  area.use(sveltePlugin);
  area.use(render);

  const a = await editor.addNode({
    id: 'a',
    label: 'A',
    inputs: {},
    outputs: {}
  });

  await area.translate(a.id, { x: 0, y: 0 });

  const socket = new ClassicPreset.Socket('socket');

  const inputControl = new ClassicPreset.InputControl('text', {
    initial: '0'
  });
  await a.addControl('text', inputControl);

  const buttonControl = new ClassicPreset.Control('button', {
    label: 'Set random',
    data: {}
  });
  await a.addControl('button', buttonControl);

  const progressControl = new ClassicPreset.Control('progress', {
    data: {}
  });
  await a.addControl('progress', progressControl);

  await render.addControl(buttonControl, Button, (_, change) => {
    const value = Math.ceil(Math.random() * 100);

    inputControl.setValue(value.toString());
    progressControl.data.progress = value;
    change();
  });

  await render.addControl(progressControl, Progress);

  return {
    destroy: () => area.destroy()
  };
}
```

