type Schemes = GetSchemes<VueArea2D<any>, VueNode<any>>

type Node = Schemes['Node'] & DataflowNode
type Conn = Schemes['Connection']
type Area = VueArea2D<Schemes>

class NumberNode extends DataflowNode {
  constructor(initial: number, change?: (val: number) => void) {
    super('Number');
    this.addControl(
      'num',
      new VuePresets.InputControl('number', { initial, change })
    );
  }

  data() {
    return {
      num: (this.controls['num'] as VuePresets.InputControl<'number'>).value,
    };
  }
}

class CustomNode extends DataflowNode {
  constructor(change?: (val: number) => void) {
    super('Custom controls');
    const input = new VuePresets.InputControl('number', { initial: 0, change });

    this.addControl('button', new ButtonControl('Set random', () => {
      const value = Math.random();

      input.setValue(value);
    }));
    this.addControl('input', input);
    this.addControl('progress', new RadialProgressControl(input));
  }
}

const socket = new Presets.ClassicPreset.Socket('socket');

export async function createNodeComponent(
  editor: NodeEditor<Schemes>,
  { node, bindControl, bindSocket }: VuePlugin.ExtractNodeProps<Schemes>
) {
  const component = VuePresets.classic.Node as VuePlugin.Component<
    Schemes['Node']
  >;

  return (
    <component
      data-testid={`node-${node.id}`}
      {...component.setup?.({
        editor,
        node,
        bindControl,
        bindSocket,
      })}
    />
  );
}

export async function createEditor(container: HTMLElement) {
  const editor = new NodeEditor<Schemes>();
  const area = new AreaPlugin<Schemes>(container);
  const connection = new ConnectionPlugin<Schemes>();
  const render = new VuePlugin<Schemes>();
  const engine = new DataflowEngine<Schemes>();

  AreaExtensions.selectableNodes(area, AreaExtensions.selector(), {
    accumulating: AreaExtensions.accumulateOnCtrl(),
  });

  render.addPreset(VuePresets.classic.setup());
  render.addPreset(VuePresets.contextMenu.setup());

  editor.use(engine);
  editor.use(area);
  area.use(connection);
  area.use(render);

  render.addNode(createNodeComponent);

  const numberNode = new NumberNode(0);
  const customNode = new CustomNode();

  await editor.addNode(numberNode);
  await editor.addNode(customNode);

  AreaExtensions.zoomAt(area, editor.getNodes());

  return {
    destroy: () => area.destroy(),
  };
}

```

