TITLE: Menubar Component Usage Example
DESCRIPTION: Complete example showing how to implement a menubar with multiple menus, submenus, checkboxes, and radio items using React Native
SOURCE: https://github.com/roninoss/rn-primitives/blob/main/apps/docs/src/content/docs/menubar.mdx#2025-04-21_snippet_1

LANGUAGE: tsx
CODE:
```
import * as React from 'react';
import { StyleSheet, View, Text } from 'react-native';
import Animated, { FadeIn } from 'react-native-reanimated';
import * as MenubarPrimitive from '@rn-primitives/menubar';

function Example() {
  const [value, setValue] = React.useState<string | undefined>();
  const [isSubOpen, setIsSubOpen] = React.useState(false);
  const [isChecked, setIsChecked] = React.useState(false);
  const [radio, setRadio] = React.useState('michael');

  function closeSubs() {
    setIsSubOpen(false);
  }

  function onValueChange(val: string | undefined) {
    if (typeof val === 'string') {
      setValue(val);
      return;
    }
    closeSubs();
    setValue(undefined);
  }

  return (
    <>
      {!!value && (
        <MenubarPrimitive.Overlay
          style={StyleSheet.absoluteFillObject}
        />
      )}
      <MenubarPrimitive.Root value={value} onValueChange={onValueChange}>
        <MenubarPrimitive.Menu value='file'>
          <MenubarPrimitive.Trigger onPress={closeSubs}>
            <Text>File</Text>
          </MenubarPrimitive.Trigger>
          <MenubarPrimitive.Portal>
            <MenubarPrimitive.Content>
              <MenubarPrimitive.Item>
                <Text>New Tab</Text>
              </MenubarPrimitive.Item>
              <MenubarPrimitive.Item>
                <Text>New Window</Text>
              </MenubarPrimitive.Item>
              <MenubarPrimitive.Item disabled>
                <Text>New Incognito Window</Text>
              </MenubarPrimitive.Item>
              <MenubarPrimitive.Separator />
              <MenubarPrimitive.Sub open={isSubOpen} onOpenChange={setIsSubOpen}>
                <MenubarPrimitive.SubTrigger>
                  <Text>Share</Text>
                </MenubarPrimitive.SubTrigger>
                <MenubarPrimitive.SubContent>
                  <Animated.View entering={FadeIn.duration(200)}>
                    <MenubarPrimitive.Item>
                      <Text>Email link</Text>
                    </MenubarPrimitive.Item>
                    <MenubarPrimitive.Item>
                      <Text>Messages</Text>
                    </MenubarPrimitive.Item>
                    <MenubarPrimitive.Item>
                      <Text>Notes</Text>
                    </MenubarPrimitive.Item>
                  </Animated.View>
                </MenubarPrimitive.SubContent>
              </MenubarPrimitive.Sub>
              <MenubarPrimitive.Separator />
              <MenubarPrimitive.Item>
                <Text>Print...</Text>
              </MenubarPrimitive.Item>
            </MenubarPrimitive.Content>
          </MenubarPrimitive.Portal>
        </MenubarPrimitive.Menu>
        <MenubarPrimitive.Menu value='edit'>
          <MenubarPrimitive.Trigger onPress={closeSubs}>
            <Text>Edit</Text>
          </MenubarPrimitive.Trigger>
          <MenubarPrimitive.Content className='native:w-48'>
            <MenubarPrimitive.Item>
              <Text>Undo</Text>
            </MenubarPrimitive.Item>
            <MenubarPrimitive.Item>
              <Text>Redo</Text>
            </MenubarPrimitive.Item>
