TITLE: Using ToggleGroupUtils in a React Native Component
DESCRIPTION: Demonstrates how to use ToggleGroupUtils functions in a custom Item component for handling toggle group behavior. It includes logic for single and multiple selection types.
SOURCE: https://github.com/roninoss/rn-primitives/blob/main/apps/docs/src/content/docs/utils.mdx#2025-04-21_snippet_1

LANGUAGE: tsx
CODE:
```
import { ToggleGroupUtils } from '@rn-primitives/hooks';
import type { PressableRef, SlottablePressableProps } from '@rn-primitives/types';

const Item = React.forwardRef<PressableRef, SlottablePressableProps>(
  (
    { value: itemValue, onPress: onPressProp, ...props },
    ref
  ) => {
    const { type, value, onValueChange } = useRootContext();

    function onPress(ev: GestureResponderEvent) {
      if (type === 'single') {
        onValueChange(ToggleGroupUtils.getNewSingleValue(value, itemValue));
      }
      if (type === 'multiple') {
        onValueChange(ToggleGroupUtils.getNewMultipleValue(value, itemValue));
      }
      onPressProp?.(ev);
    }

    const isChecked =
      type === 'single' ? ToggleGroupUtils.getIsSelected(value, itemValue) : undefined;
    const isSelected =
      type === 'multiple' ? ToggleGroupUtils.getIsSelected(value, itemValue) : undefined;

    return (
        <Pressable
          ref={ref}
          role={type === 'single' ? 'radio' : 'checkbox'}
          onPress={onPress}
          aria-checked={isChecked}
          aria-selected={isSelected}
          accessibilityState={{
            checked: isChecked,
            selected: isSelected,
          }}
          {...props}
        />
      </ItemContext.Provider>
    );
  }
);
```

