# #![allow(unused_variables)]
# pub mod x {
# pub trait Trait {}
#
# struct MyType;
#
# impl Trait for MyType {}
#
# pub type Alias = impl Trait;
#
# #[define_opaque(Alias)]
# pub fn new() -> Alias {
#     MyType
# }
# }
# use x::*;
// this...
pub fn take_alias(x: Alias) {
    // ...
}

