TITLE: Integrating Tickets Data Table in Next.js Page Component
DESCRIPTION: This server-side Next.js page component fetches support tickets data using a Supabase client and renders the TicketsDataTable component. It handles pagination and search query parameters, demonstrating the integration of server-side data fetching with client-side rendering.
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/courses/realtime_updates_server_components_7.md#2025-04-22_snippet_21

LANGUAGE: tsx
CODE:
```
import { use } from 'react';

import { getSupabaseServerClient } from '@kit/supabase/server-client';
import { PageBody, PageHeader } from '@kit/ui/page';

import { createTicketsService } from '~/lib/server/tickets/tickets.service';

import { TicketsDataTable } from './_components/tickets-data-table';

interface TicketsPageProps {
  params: {
    account: string;
  };

  searchParams: {
    page?: string;
    query?: string;
  };
}

export default function TicketsPage(props: TicketsPageProps) {
  const client = getSupabaseServerClient();
  const service = createTicketsService(client);

  const page = Number(props.searchParams.page ?? '1');
  const query = props.searchParams.query ?? '';

  const { data, pageSize, pageCount } = use(
    service.getTickets({
      accountSlug: props.params.account,
      page,
      query,
    }),
  );

  return (
    <>
      <PageHeader
        title={'Support Tickets'}
        description={
          'Here is the list of the support tickets from your customers'
        }
      />

      <PageBody>
        <TicketsDataTable
          pageIndex={page - 1}
          pageCount={pageCount}
          pageSize={pageSize}
          data={data}
        />
      </PageBody>
    </>
  );
}
```

