TITLE: Creating Tickets Service (TypeScript)
DESCRIPTION: This snippet defines a TicketsService class that encapsulates the logic for fetching support tickets from the database. It includes methods for pagination, filtering by account, and text search.
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/courses/realtime_updates_server_components_7.md#2025-04-22_snippet_16

LANGUAGE: tsx
CODE:
```
import { SupabaseClient } from '@supabase/supabase-js';

import { Database } from '~/lib/database.types';

export function createTicketsService(client: SupabaseClient<Database>) {
  return new TicketsService(client);
}

class TicketsService {
  constructor(private readonly client: SupabaseClient<Database>) {}

  async getTickets(params: {
    accountSlug: string;
    page: number;
    limit?: number;
    query?: string;
  }) {
    const limit = params.limit ?? 10;
    const startOffset = (params.page - 1) * limit;
    const endOffset = startOffset + limit;

    let query = this.client
      .from('tickets')
      .select('*, account_id !inner (slug)', {
        count: 'exact',
      })
      .eq('account_id.slug', params.accountSlug)
      .order('created_at', { ascending: false })
      .range(startOffset, endOffset);

    if (params.query) {
      query = query.textSearch('title', `"${params.query}"`);
    }

    const { data, error, count } = await query;

    if (error) {
      throw error;
    }

    return {
      data: data ?? [],
      count: count ?? 0,
      pageSize: limit,
      page: params.page,
      pageCount: Math.ceil((count ?? 0) / limit),
    };
  }
}
