TITLE: Implementing Onboarding Completion Page in Next.js
DESCRIPTION: React component that handles the onboarding completion page, checking user's onboarding status and redirecting to home page when complete. Uses React Query for polling the user's status with a maximum of 10 attempts.
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/kits/waitlist_plugin_plugins_drizzle_supabase_52.md#2025-04-22_snippet_19

LANGUAGE: tsx
CODE:
```
'use client';

import { useRef } from 'react';

import { useQuery } from '@tanstack/react-query';

import { useSupabase } from '@kit/supabase/hooks/use-supabase';
import { LoadingOverlay } from '@kit/ui/loading-overlay';

import pathsConfig from '~/config/paths.config';

export default function OnboardingCompletePage() {
  const { error } = useCheckUserOnboarded();

  if (error) {
    return (
      <div className={'flex flex-col items-center justify-center'}>
        <p>Something went wrong...</p>
      </div>
    );
  }

  return <LoadingOverlay>Setting up your account...</LoadingOverlay>;
}

function useCheckUserOnboarded() {
  const client = useSupabase();
  const countRef = useRef(0);
  const maxCount = 10;
  const error = countRef.current >= maxCount;

  useQuery({
    queryKey: ['onboarding-complete'],
    refetchInterval: () => (error ? false : 1000),
    queryFn: async () => {
      if (error) {
        return false;
      }

      countRef.current++;

      const response = await client.auth.getUser();

      if (response.error) {
        throw response.error;
      }

      const onboarded = response.data.user.app_metadata.onboarded;

      if (onboarded) {
        return window.location.assign(pathsConfig.app.home);
      }

      return false;
    },
  });

  return {
    error,
  };
}
```

