    const currentClaims = user.customClaims || {};

    await auth.setCustomUserClaims(
      userId,
      merge
        ? {
            ...currentClaims,
            ...claims,
          }
        : claims,
    );

    console.log(`Claims set successfully`);
  } catch (e) {
    console.error(e);
  }

  process.exit();
}

