TITLE: Building Ticket Status Form Component in React TSX
DESCRIPTION: Creates a React component for the ticket status form using React Hook Form and Shadcn UI components. It handles form submission and status updates.
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/courses/data_fetching_3.md#2025-04-22_snippet_28

LANGUAGE: tsx
CODE:
```
'use client';

// all the imports previously added ...

import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
} from '@kit/ui/form';
import { Heading } from '@kit/ui/heading';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@kit/ui/select';

import { Tables } from '~/lib/database.types';

import { UpdateTicketStatusSchema } from '../_lib/schema/update-ticket-status.schema';
import { updateTicketStatusAction } from '../_lib/server/server-actions';

// ... rest of the code

function StatusSelect(props: {
  status: Tables<'tickets'>['status'];
  ticketId: string;
  disabled: boolean;
}) {
  const form = useForm({
    resolver: zodResolver(UpdateTicketStatusSchema),
    defaultValues: {
      status: props.status,
      ticketId: props.ticketId,
    },
  });

  const [pending, startTransition] = useTransition();

  return (
    <Form {...form}>
      <FormField
        render={({ field }) => {
          return (
            <FormItem>
              <FormLabel>Status</FormLabel>

              <FormControl>
                <Select
                  value={form.getValues('status')}
                  disabled={pending || props.disabled}
                  onValueChange={(value) => {
                    form.setValue(
                      field.name,
                      value as Tables<'tickets'>['status'],
                      {
                        shouldValidate: true,
                      },
                    );

                    void form.handleSubmit((value) => {
                      startTransition(async () => {
                        await updateTicketStatusAction(value);
                      });
                    })();
                  }}
                >
                  <SelectTrigger>
                    <SelectValue placeholder={'Status'} />
                  </SelectTrigger>

                  <SelectContent>
                    <SelectItem value={'open'}>Open</SelectItem>
                    <SelectItem value={'closed'}>Closed</SelectItem>
                    <SelectItem value={'in_progress'}>In Progress</SelectItem>
                    <SelectItem value={'resolved'}>Resolved</SelectItem>
                  </SelectContent>
                </Select>
              </FormControl>

              <FormDescription>
                The status of the ticket determines its current state.
              </FormDescription>
            </FormItem>
          );
        }}
        name={'status'}
      />
    </Form>
  );
}
```

