TITLE: Implementing a Server Action to Add Tasks to Supabase
DESCRIPTION: Creates a server action function that adds task data to a Supabase database. It includes user authentication, data validation with Zod, error handling, and page revalidation after successful database operation.
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/kits/marketing_pages_migrations_permissions_and_roles_45.md#2025-04-22_snippet_12

LANGUAGE: tsx
CODE:
```
'use server';

import { revalidatePath } from 'next/cache';
import { redirect } from 'next/navigation';

import { z } from 'zod';

import { getLogger } from '@kit/shared/logger';
import { requireUser } from '@kit/supabase/require-user';
import { getSupabaseServerClient } from '@kit/supabase/server-client';

import { WriteTaskSchema } from '~/(dashboard)/home/(user)/_lib/schema/write-task.schema';

export async function addTaskAction(params: z.infer<typeof WriteTaskSchema>) {
  'use server';

  const task = WriteTaskSchema.parse(params);

  const logger = await getLogger();
  const client = getSupabaseServerClient();
  const auth = await requireUser(client);

  if (!auth.data) {
    redirect(auth.redirectTo);
  }

  logger.info(task, `Adding task...`);

  const { data, error } = await client
    .from('tasks')
    .insert({ ...task, account_id: auth.data.id });

  if (error) {
    logger.error(error, `Failed to add task`);

    throw new Error(`Failed to add task`);
  }

  logger.info(data, `Task added successfully`);

  revalidatePath('/home', 'page');

  return null;
}
```

