TITLE: Implementing Full Tasks Page in React with Remix
DESCRIPTION: This is the complete implementation of the Tasks page, including imports, loader function for data fetching, action function for mutations, and the main component rendering the page layout and content.
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/kits/supabase_data_fetching_supabase_data_writing_tailwind_css_86.md#2025-04-22_snippet_19

LANGUAGE: tsx
CODE:
```
import type { ActionArgs, MetaFunction } from '@remix-run/node';

import { json } from '@remix-run/node';
import { Form, useFetcher, useLoaderData } from '@remix-run/react';
import type { LoaderArgs } from '@remix-run/server-runtime';

import { useCallback } from "react";

import {
  PlusCircleIcon,
  RectangleStackIcon,
} from '@heroicons/react/24/outline';

import { Trans } from 'react-i18next';

import AppHeader from '~/components/AppHeader';
import AppContainer from '~/components/AppContainer';
import getSupabaseServerClient from '~/core/supabase/server-client';
import { getTasks } from '~/lib/tasks/queries';
import getCurrentOrganization from '~/lib/server/organizations/get-current-organization';

import If from '~/core/ui/If';
import Heading from '~/core/ui/Heading';
import Button from '~/core/ui/Button';
import Modal from '~/core/ui/Modal';
import TextField from '~/core/ui/TextField';

import type Task from '~/lib/tasks/types/task';
import TasksTable from '~/components/tasks/TasksTable';
import TaskForm from '~/components/tasks/TaskForm';

import { createTask, deleteTask, updateTask } from '~/lib/tasks/mutations';
import { throwNotFoundException } from '~/core/http-exceptions';

export const meta: MetaFunction = () => {
  return [
    {
      title: 'Tasks',
    },
  ];
};

export async function loader(args: LoaderArgs) {
  const request = args.request;

  const url = new URL(request.url);
  const searchParams = url.searchParams;
  const page = searchParams.get('page') ?? '1';
  const query = searchParams.get('query') ?? '';

  const client = getSupabaseServerClient(request);
  const data = await getCurrentOrganization(client);

  const pageSize = 8;
  const pageIndex = Number(page) - 1;
  const organizationId = data?.organization.id;

  if (!organizationId) {
    return new Response('Not found', {
      status: 404,
    });
  }

  const {
    data: tasks,
    count,
    error,
  } = await getTasks(client, {
    organizationId: data.organization.id,
    pageIndex,
    query,
  });

  if (error) {
    console.error(error);

    return json({
      tasks: [],
      count: 0,
      pageCount: 0,
      query,
      pageIndex,
      pageSize,
    });
  }

  const pageCount = count ? Math.ceil(count / pageSize) : 0;

  return json({
    tasks,
    count,
    query,
    pageCount,
    pageIndex,
    pageSize,
  });
}

function TasksPage() {
  const { tasks, count, pageCount, query, pageIndex, pageSize } =
    useLoaderData<typeof loader>();

  return (
    <>
      <AppHeader>
        <span className={'flex space-x-2'}>
          <RectangleStackIcon className="w-6" />

          <span>
            <Trans i18nKey={'common:tasksTabLabel'} />
          </span>
        </span>
      </AppHeader>

      <AppContainer>
        <If condition={!count}>
          <TasksEmptyState />
        </If>

        <TasksTableContainer
          pageSize={pageSize}
          pageIndex={pageIndex}
          pageCount={pageCount}
          tasks={tasks}
          query={query}
        />
      </AppContainer>
    </>
  );
}

export default TasksPage;

function TasksTableContainer({
  tasks,
  pageCount,
  pageIndex,
  pageSize,
  query,
}: React.PropsWithChildren<{
  tasks: Task[];
  pageCount: number;
  pageIndex: number;
  pageSize: number;
  query?: string;
}>) {
  return (
    <div className={'flex flex-col space-y-4'}>
      <div className={'flex space-x-4 justify-between items-center'}>
        <div className={'flex'}>
          <CreateTaskModal>
            <Button color={'transparent'}>
              <span className={'flex space-x-2 items-center'}>
                <PlusCircleIcon className={'w-4'} />

                <span>New Task</span>
              </span>
            </Button>
          </CreateTaskModal>
        </div>

        <Form className={'w-full max-w-sm'} method={'GET'}>
          <TextField.Input
            defaultValue={query}
            name={'query'}
            className={'w-full'}
            placeholder={'Search for task...'}
          />
        </Form>
      </div>

      <TasksTable
        pageSize={pageSize}
        pageIndex={pageIndex}
        pageCount={pageCount}
        tasks={tasks}
      />
    </div>
  );
}

function TasksEmptyState() {
  return (
    <div className={'flex flex-col space-y-8 p-4'}>
      <div className={'flex flex-col'}>
        <Heading type={2}>
          <span className={'font-semibold'}>
            Hey, it looks like you don&apos;t have any tasks yet... 
          </span>
        </Heading>

        <Heading type={4}>
          Create your first task by clicking on the button below
        </Heading>
      </div>
    </div>
  );
}

function CreateTaskModal(props: React.PropsWithChildren) {
  const fetcher = useFetcher();

  const submitting = fetcher.state === 'submitting';

  const onSubmit = useCallback(
    (task: Omit<Task, 'id'>) => {
      fetcher.submit(task, {
        method: 'POST',
        encType: 'application/json',
      });
    },
    [fetcher],
  );

  return (
    <Modal heading={`Create Task`} Trigger={props.children}>
      <TaskForm onSubmit={onSubmit} submitting={submitting} />
    </Modal>
  );
}

export async function action(args: ActionArgs) {
  const client = getSupabaseServerClient(args.request);
  const body = await args.request.json();

  const ok = () => json({ success: true });

  switch (args.request.method) {
    case 'POST':
      return createTask(client, body).then(ok);

    case 'DELETE':
      return deleteTask(client, body).then(ok);

    case 'PUT':
      return updateTask(client, body).then(ok);
  }

  return throwNotFoundException();
}
```

