TITLE: Widget Container Component Implementation
DESCRIPTION: React component that provides widget context and handles mounting logic with local storage integration
SOURCE: https://github.com/sahilpanhotra/makerkitdocs.git/blob/main/mdDocs/courses/building_javascript_widget_1.md#2025-04-22_snippet_10

LANGUAGE: tsx
CODE:
```
'use client';

import { useEffect, useState } from 'react';

import { WidgetContext } from '../lib/context';
import SupportTicketWidgetContainer from './support-ticket-widget-container';

export default function WidgetContainer(props: { accountId: string }) {
  const [mounted, setMounted] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [ticketId, setTicketId] = useState(getTicketIdFromLocalStorage());

  useEffect(() => {
    setMounted(true);
  }, []);

  if (!mounted) {
    return null;
  }

  const context = {
    isOpen,
    setIsOpen,
    ticketId,
    setTicketId: (ticketId: string) => {
      localStorage.setItem('ticketId', ticketId);
      setTicketId(ticketId);
    },
  };

  return (
    <WidgetContext.Provider value={context}>
      <SupportTicketWidgetContainer accountId={props.accountId} />
    </WidgetContext.Provider>
  );
}

function getTicketIdFromLocalStorage() {
  return localStorage.getItem('ticketId') ?? '';
}
```

