sap.ui.require(["sap/ui/core/format/NumberFormat"], function(NumberFormat) {
   var oUnitFormat = NumberFormat.getUnitInstance();
   oUnitFormat.format(12345.678, "speed-mile-per-hour"); // output: 12,345.678 mph
