"$MaxLength" : <6.2.2 MaxLength>, // optional, number
"$Precision" : <6.2.3 Precision>, // optional, number
"$Scale" : <6.2.4 Scale> | "variable", // optional, number or fixed string
"$SRID" : "<6.2.6 SRID>", // optional
"@..." : <value>
}
