        "id": "<id>",
        "type": "application",
        "i18n": "<i18nPathRelativeToManifest>",
        "applicationVersion": {
            "version": "<version>"
        },
        "title": "{{<title>}}",
        "tags": {
            "keywords": [
                "{{<keyword1>}}", "{{<keyword2>}}"
            ]
        },
        "dataSources": {
