  function(SupportLib) {
    "use strict";
    var rule1 = {
      ...
      audiences: [SupportLib.Audiences.Control],
      categories: [SupportLib.Categories.Usability],
        ...
      check: function(oIssueManager, ...) {
        ...
        oIssueManager.addIssue({
          severity: SupportLib.Severity.Medium,
          ...
        });
      }
    };

    var rule2 = {};

    return {
      name: "sap.ui.core",
      niceName: "UI5 Core Library",
      ruleset: [
        rule1,
        rule2
      ]
    };
  }, true);
