TITLE: Implementing Root Attribute Forwarding in TypeScript
DESCRIPTION: This snippet defines utility functions for forwarding attributes in Vue components. It includes methods to omit specific attributes, get non-event attributes, and get event attributes. These functions are crucial for implementing root attribute forwarding in Onyx components.
SOURCE: https://github.com/schwarzit/onyx/blob/main/apps/docs/src/principles/contributing/patterns.md#2025-04-23_snippet_0

LANGUAGE: typescript
CODE:
```
import { getCurrentInstance, nextTick } from "vue";
import type { ComponentInternalInstance } from "vue";

/**
 * Get all attributes except the ones specified
 */
export function useOmit(omit: string[] = []) {
  return useAttrs((attrs) =>
    Object.fromEntries(
      Object.entries(attrs).filter(([key]) => !omit.includes(key))
    )
  );
}

/**
 * Get all non-event attributes
 */
export function useNonEventAttrs() {
  return useAttrs((attrs) =>
    Object.fromEntries(
      Object.entries(attrs).filter(([key]) => !key.startsWith("on"))
    )
  );
}

/**
 * Get all event attributes
 */
export function useEventAttrs() {
  return useAttrs((attrs) =>
    Object.fromEntries(
      Object.entries(attrs).filter(([key]) => key.startsWith("on"))
    )
  );
}

function useAttrs(filterFn: (attrs: Record<string, unknown>) => Record<string, unknown>) {
  const instance = getCurrentInstance();
  if (!instance) {
    throw new Error("useAttrs() can only be used inside setup() or functional components!");
  }
  return () => {
    nextTick(() => instance.update());
    return filterFn(instance.proxy?.$attrs ?? {});
  };
}

/**
 * Get the emitter function for a specific event
 */
export function useEmitFn(instance: ComponentInternalInstance | null = getCurrentInstance()) {
  if (!instance) {
    throw new Error("No instance provided and not in setup() context");
  }
  return (name: string, ...args: unknown[]) => instance.emit(name, ...args);
}
```

