/**
 * OnyxComponent can be used to render something.
 * This is rendered into the "Info" panel in Storybook.
 */
const meta: Meta<typeof OnyxComponent> = {
  title: 'Components/OnyxComponent',
  component: OnyxComponent,
  argTypes: {
    modelValue: {
      control: {
        type: 'select',
        options: ['1', '2', '3'],
      },
      description: 'Overwrites the automatically inferred description.',
    },
  },
  args: {
    // Default args that are applied to all Stories
    disabled: false,
  },
  // Log interesting native DOM events
  decorators: [
    withNativeEventLogging(['touchend', 'click', 'keyup', 'change']),
  ],
}

export default meta
```

