LANGUAGE: typescript
CODE:
```
/**
 * @package
 */
export function useBrowserArgumentsFrom(browserArgs: string[]): (existingArgs: string[]) => string[] {
    return (existingArgs: string[]) => {
        return [
            ...existingArgs,
            ...browserArgs,
        ];
    };
}
```

