export function getLocaleFromRequest(request: Request): Locale {
  const url = new URL(request.url);

  switch (url.host) {
    case 'ca.hydrogen.shop':
      return {
        language: 'EN',
        country: 'CA',
      };
      break;
    case 'hydrogen.au':
      return {
        language: 'EN',
        country: 'AU',
      };
      break;
    default:
      return {
        language: 'EN',
        country: 'US',
      };
  }
}
