class SetOrderStateAction extends FlowAction implements DelayableAction
{
    public function handleFlow(StorableFlow $flow): void
    {
        $this->connection->beginTransaction();

        //do stuff

        try {
            $this->connection->commit();
        } catch (\Throwable $e) {

        }
    }
}
